var React = require('react-native');
var {
 ListView,
 StyleSheet,
} = React;

var TodoListItem = require('components/todo-list-item');

var styles = StyleSheet.create({
  container: {
    flex: 1
  }
});

var TodoList = React.createClass({
  render: function() {
    let ds = new ListView.DataSource({rowHasChanged: (r1, r2) => r1 !== r2});
    ds = ds.cloneWithRows(this.props.children);
    return <ListView style={styles.container} dataSource={ds} renderRow={(child) => <TodoListItem todo={child} data={this.props.data} listClass={TodoList} navigator={this.props.navigator} />} />;
  }
});

module.exports = TodoList;
